var restify = require('./lib');

var s = restify.createServer();

s.put('/foo', function (req, res, next) {
        res.send(202);
        next();
});

s.put(/.*/, function (req, res, next) {
        res.send(200);
        next();
});

s.on('after', restify.auditLogger({
        log: require('bunyan').createLogger({
                name: 'foo'
        })
}))

s.listen(8080, function () {
        console.log(s.url);
});